import React, { useEffect } from "react";
import { axios } from "./axios";
import { Scatter } from "react-chartjs-2";

export default function Visualize() {
  //   const [visualise, setVisualise] = useState([]);
  let dropOffData = [];
  const popularPointData = {
    labels: ["Scatter"],
    datasets: [
      {
        label: "My First dataset",
        fill: false,
        backgroundColor: "rgba(75,192,192,0.4)",
        pointBorderColor: "rgba(75,192,192,1)",
        pointBackgroundColor: "#000000",
        pointBorderWidth: 5,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: "rgba(75,192,192,1)",
        pointHoverBorderColor: "rgba(220,220,220,1)",
        pointHoverBorderWidth: 2,
        pointRadius: 1,
        pointHitRadius: 10,
        data: [
          { x: 13.406305710121323, y: 52.54096873175922 },
          { x: 13.4446535030306, y: 52.545381856568476 },
          { x: 13.365558432116691, y: 52.53337051722053 },
          { x: 13.466694041753325, y: 52.559273663322855 },
          { x: 13.42080856393584, y: 52.53317402906312 },
          { x: 13.493951208041736, y: 52.541230104140276 },
          { x: 13.407925477070265, y: 52.53687031832495 }
        ]
      },
      {
        label: "My First dataset",
        fill: true,
        backgroundColor: "rgba(98,220,43,0.4)",
        pointBorderColor: "rgba(75,192,192,1)",
        pointBackgroundColor: "#2345678",
        pointBorderWidth: 15,
        pointHoverRadius: 65,
        pointHoverBackgroundColor: "rgba(23,986,12,1)",
        pointHoverBorderColor: "rgba(110,90,20,1)",
        pointHoverBorderWidth: 2,
        pointRadius: 1,
        pointHitRadius: 1,
        data: [
          { x: 13.406305710121323, y: 52.54096873175922 },
          { x: 13.4446535030306, y: 52.545381856568476 },
          { x: 13.365558432116691, y: 52.53337051722053 },
          { x: 13.466694041753325, y: 52.559273663322855 },
          { x: 13.42080856393584, y: 52.53317402906312 },
          { x: 13.493951208041736, y: 52.541230104140276 },
          { x: 13.407925477070265, y: 52.53687031832495 }
        ]
      }
    ]
  };
  const getVisualise = async () => {
    let data = {};
    const response = await axios
      .get("/")
      .catch(err => console.log("Error", err));
    // console.log(response.data.most_popular_dropoff_points);
    if (response.data) {
      let obj = JSON.parse(response.data.most_popular_dropoff_points);
      obj.features.forEach(feature => {
        data = {
          x: feature.geometry.coordinates[0],
          y: feature.geometry.coordinates[1]
        };
        dropOffData.push(data);
      });
      console.log(dropOffData);
      // console.log(obj.features[0].properties.name);
      // console.log(obj.features[0].geometry.coordinates);
      //   console.log(response.data.most_popular_dropoff_points.geometry);
    }
  };
  useEffect(() => {
    getVisualise();
  }, []);
  return (
    <div>
      <button onClick={getVisualise}>Simulate</button>

      <Scatter data={popularPointData} />
    </div>
  );
}
